    width: "80%",
    marginLeft: "10%"
}


function App() {
    const next = document.getElementById("next-content")
    const prev = document.getElementById("prev-content")

    const [current, setCount] = useState(0);

    if (current == 0) {
        prev.style.visibility = "hidden"
    }

    const increment = () => {
        if (current === 2) {
            next.style.visibility = "hidden"
        }
        if (current < 4) {
            setCount(current+1);
        } 
        if (current == 1) {
            prev.style.visibility = "hidden"
        }
     }

    const decrement = () => {
