import React, {useState} from "react";
import work from './work';
import Header from "./components/Header"
import Subheader from "./components/Subheader"
import List from "./components/List"

const imgStyle = 
{ 
    width: "80%",
    marginLeft: "10%"
}

function App() {
    const [current, changeCounter] = useState(0);
    const next = document.getElementById("next-content")
    const prev = document.getElementById("prev-content")

    function increaseCounter() {
        changeCounter(current + 1);
        if (current == 2) {next.style.visibility = "hidden" }
    }

    // function decreaseCounter() {
    //     if (current == 0) {
    //         changeCounter(3);
    //     }
    //     changeCounter(current - 1);
    // }

    next.addEventListener("click", function increaseCount(event) {
        if (current<3) { 
            increaseCounter(); 
        }
        event.preventDefault();
    });

    // prev.addEventListener("click", function increaseCount(event) {
    //     decreaseCounter();
    //     event.preventDefault();
    // });

    return (
    <div>
    {console.log(current)}
        <Header content={work[current].title}/>
        <Subheader company={work[current].company} date={work[current].date}/>
        <ul>
            {work[current].list.map(bullet => (
                <List
                key={bullet.key}
                content={bullet.content}
                />
            ))}
        </ul>
        <img src={work[current].url} style={imgStyle}></img>
    </div>
    )
}

export default App;
