import React, {useState} from "react";
import work from './work';
import Header from "./components/Header"
import Subheader from "./components/Subheader"
import List from "./components/List"

const imgStyle = 
{ 
    width: "80%",
    marginLeft: "10%"
}

function App() {
    var [current, changeCounter] = useState(0);
    var next = document.getElementById("next-content")
    var prev = document.getElementById("prev-content")

    function updateCounter(e) {
        changeCounter((prevValue) => {
            if (e.target.id === "next-content") {
                return prevValue + 1
            } else {
                return prevValue - 1
            }
        })
    }

    function decrement() {
        changeCounter(current-1);
    }

    next.addEventListener("click", function increaseCount(event) {
        updateCounter(event);
        event.preventDefault();
    });

    prev.addEventListener("click", function decreaseCounter(event) {
        updateCounter(event);
        event.preventDefault();
    });

    return (
    <div>
    {console.log(current)}
        <Header content={work[current].title}/>
        <Subheader company={work[current].company} date={work[current].date}/>
        <ul>
            {work[current].list.map(bullet => (
                <List
                key={bullet.key}
                content={bullet.content}
                />
            ))}
        </ul>
        <img src={work[current].url} style={imgStyle}></img>
    </div>
    )
}

export default App;
