import React, {useState} from "react";
import work from './work';
import Header from "./components/Header"
import Subheader from "./components/Subheader"
import List from "./components/List"

const imgStyle = { width: "90%" }

function App() {
    var [current, changeCounter] = useState(0);
    var button = document.getElementById("change-content")

    function updateCounter() {
        changeCounter(current+1);
    }

    button.addEventListener("click", function increaseCount(event) {
        updateCounter();
        event.preventDefault();
    });

    return (
    <div>
        <Header content={work[current].title}/>
        <Subheader company={work[current].company} date={work[current].date}/>
        <ul>
            {work[current].list.map(bullet => (
                <List
                key={bullet.key}
                content={bullet.content}
                />
            ))}
        </ul>
        <img src={work[current].url} style={imgStyle}></img>
    </div>
    )
}

export default App;
