const experiences = [
    {
      key: 1,
      title: "Designer",
      company: "BME161 Group Project",
      data: "Sept 2020-Dec 2020",
      list: [
        "Design a wheelchair attachment to carry a 6 to 18-month old infant without inhibiting wheelchair usage.",
        "Utilized iterative design, conducted user/market research, and created personas to empathize with the users (paraplegic parents).",
        "Analyzed competitor products and the design requirements using various tools (QFD, Best-Of-Class Chart, etc) to improve the design."
      ]
    },
    {
        key: 2,
        title: "Founder/Co-Director",
        company: "Eye Hope Canada",
        data: "Nov 2020-May 2021",
        list: [
          "Published Resilience: Stories of Cancer Survivorship, a collection of illustrations, poems, and short stories about the experiences of 14 cancer survivors and activists. Donated the profits to Childhood Cancer Canada.",
          "Organized social media advertisements, created sponsorship materials and managed finances.",
          "Led a team of 50 students. Raised over $1500 in donations for Canadian Retinoblastoma Society, POGO, and SickKids."
        ]
      }
  ];

  export default experiences;
