import React, {useState} from "react";
import work from './work';
import Header from "./components/Header"
import Subheader from "./components/Subheader"
import List from "./components/List"

const imgStyle = 
{ 
    width: "80%",
    marginLeft: "10%"
}

const next = document.getElementById("next-content")
const prev = document.getElementById("prev-content")

function App() {
    const [current, setCount] = useState(0);

    const increment = () => {
        setCount(current+1);
        console.log("i: " + current)

     }

    const decrement = () => {
        setCount(current-1);
        console.log("d: " + current)
    }

    // next.addEventListener("click", function test(event) {
    //     increment();
    //     event.preventDefault()
    //     console.log("test")
    // })
    // prev.addEventListener("click", decrement)

    return (
    <div>
        <a onclick={decrement} id="prev-content" type="button" className="btn btn-outline-info" >← Prev</a>
        <a onclick={increment} id="next-content" type="button" className="btn btn-outline-info" >→ Next</a>

        <Header content={work[current].title}/>
        <Subheader company={work[current].company} date={work[current].date}/>
        <ul>
            {work[current].list.map(bullet => (
                <List
                key={bullet.key}
                content={bullet.content}
                />
            ))}
        </ul>
        <img src={work[current].url} style={imgStyle}></img>
    </div>
    )
}


export default App;
