import React, {useState} from "react";
import work from './work';
import Header from "./components/Header"
import Subheader from "./components/Subheader"
import List from "./components/List"

const imgStyle = 
{ 
    width: "80%",
    marginLeft: "10%"
}

function App() {
    var [current, changeCounter] = useState({
        count:0
    });

    var next = document.getElementById("next-content")
    var prev = document.getElementById("prev-content")

    function updateCounter(e) {
        buttonId = e.target.id;
        changeCounter( (prevValue) => {
            if (buttonId === "next-content") {
                return 
                {count:prevValue.count+1}
            } else {
                return 
                {count:prevValue.count-1}
            }
        }) 
    }

    next.addEventListener("click", function increaseCount(event) {
        updateCounter();
        event.preventDefault();
    });

    prev.addEventListener("click", function decreaseCount(event) {
        updateCounter();
        event.preventDefault();
    });

    return (
    <div>
    {console.log(current.count)}
        <Header content={work[current.count].title}/>
        <Subheader company={work[current.count].company} date={work[current.count].date}/>
        <ul>
            {work[current.count].list.map(bullet => (
                <List
                key={bullet.key}
                content={bullet.content}
                />
            ))}
        </ul>
        <img src={work[current.count].url} style={imgStyle}></img>
    </div>
    )
}

export default App;
